mapper.update:
UPDATE BOARDTABLE SET
CNT = CNT + 1
WHERE BDNUM = #{bdnum}

UPDATE BOARDTABLE SET
CNT = CNT - 1
WHERE BDNUM = #{bdnum}

mapper.select:
SELECT BDNUM, TITLE, WRITER, WRITEDATE, CONTENT, PW, ISUSER, viewCount
FROM BOARDTABLE
WHERE BDNUM = #{bdnum}

mapper.update:
UPDATE BOARDTABLE SET
viewCount = viewCount + 1
WHERE BDNUM = #{bdnum}

mapper.delete:
DELETE FROM BOARDTABLE
WHERE 
	WRITER = #{writer}
	AND
	BDNUM = #{bdnum}

DELETE FROM BOARDTABLE
WHERE 
	PW = #{pw}
	AND
	BDNUM = #{bdnum}

mapper.select:
SELECT MAX(NO)
FROM COMM
WHERE BDNUM = #{bdnum}

mapper.delete:
DELETE FROM COMM
WHERE 
	NO = #{no}
	AND
	BDNUM = #{bdnum}
	AND
	PW = #{pw}

DELETE FROM COMM
WHERE 
	NO = #{no}
	AND
	BDNUM = #{bdnum}
	AND
	writer = #{writer}
